mapper.select:
SELECT uid
     , user_id
     , password
  FROM MEMBER
WHERE user_id = #{userId}
  AND password = #{password}

mapper.insert:
INSERT INTO MEMBER (
    user_id
  , password
) VALUES (
    #{userId}
  , #{password}
)

mapper.update:
UPDATE MEMBER
   SET password = #{password}
 WHERE uid = #{uid}

mapper.insert:
INSERT INTO DOC (
    member_uid
  , content
  , reg_datetime
) VALUES (
    #{memberUid}
  , #{content}
  , NOW()
)

mapper.update:
UPDATE DOC
   SET content = #{content}
 WHERE uid = #{uid}
